import React, { useState } from 'react'
import TextField from '@material-ui/core/TextField';
// import { Title } from '@material-ui/icons';

const CreatePost = () => {
    const [title, setTitle] = useState("")
    const [body, setBody] = useState("")
    const [image, setImage] = useState("")

    const formData = new FormData()
    formData.append("file",image)
    formData.append("upload_preset","insta-clone")
    formData.append("cloud_name","sitanshu")
    fetch("https://api.cloudinary.com/v1_1/sitanshu/image/upload",{
        method:'post',
        body:formData
    }).then(res=>res.json())
    .then(data=>{
        console.log(data)
    }).catch(err=>{
        console.log(err)
    })
    return (
        <>
            <div className="card input-filed">
                <TextField id="standard-basic" label="Title" value={title} onChange={(event) => { setTitle(event.target.value) }} />
                <TextField id="standard-basic" label="Body" value={body} onChange={(event) => { setBody(event.target.value) }} />
                <div class="file-field input-field">
                    <div class="btn">
                        <span>Upload Image</span>
                        <input type="file" value={image} onChange={(event) => { setImage(event.target.files[0]) }} />
                    </div>
                    <div class="file-path-wrapper">
                        <input class="file-path validate" type="text" />
                    </div>
                </div>
                <button className="btn waves-effect waves-light #64b5f6 blue lighten-2" type="submit" name="action" onClick={()=>p} >Submit</button>

            </div>
        </>
    )
}
export default CreatePost
