import React, { useContext, useRef, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { UserContext } from '../App'
// import M from 'materialize-css'
import InputBase from '@material-ui/core/InputBase';
import { fade, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';


const Navbar = () => {
    const { state, dispatch } = useContext(UserContext)
    const history = useHistory()
    const searchModal = useRef(null)
    useEffect(() => {
        // M.Modal.init(searchModal.current)
    }, [])
    const useStyles = makeStyles((theme) => ({
        
        
        searchIcon: {
            padding: theme.spacing(0, 2),
            height: '100%',
            position: 'absolute',
            pointerEvents: 'none',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
        },
        inputRoot: {
            color: 'inherit',
        },
        inputInput: {
            padding: theme.spacing(1, 1, 1, 0),
            // vertical padding + font size from searchIcon
            paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
            transition: theme.transitions.create('width'),
            width: '100%',
            [theme.breakpoints.up('sm')]: {
                width: '12ch',
                '&:focus': {
                    width: '20ch',
                },
            },
        },
    }));
    const classes = useStyles();
    const renderlist = () => {
        if (state) {
            return [<li><div className={classes.root}>
                <div className={classes.search}>
                    <div className={classes.searchIcon}>
                        <SearchIcon />
                    </div>
                    <InputBase
                        placeholder="Search…"
                        classes={{
                            root: classes.inputRoot,
                            input: classes.inputInput,
                        }}
                        inputProps={{ 'aria-label': 'search' }}
                    />
                </div>
            </div>
            </li>
                ,
            <li><Link key='2' to="/profile">Profile</Link></li>,
            <li><Link key='3' to="/creatpost">Create Post</Link></li>,
            <li>
                <button className="btn waves-effect waves-light #f44336 red" type="submit" name="action" onClick={() => {
                    localStorage.clear()
                    dispatch({ type: null })
                    history.push('/login')
                }}>LogOut</button>

            </li>
            ]
        } else {
            return [
                <li><Link key='4' to="/login">Login</Link></li>,
                <li><Link key='5' to="/signup">Signup</Link></li>,

            ]
        }
    }
    return (
        <>
            <nav>
                <div className="nav-wrapper">
                    <Link key='6' to={state ? "/" : "/login"} className="brand-logo">Instagram</Link>
                    <ul id="nav-mobile" className="right ">
                        {renderlist()}
                    </ul>
                </div>
                {/* <!-- Modal Structure --> */}
                <div id="modal1" className="modal" ref={searchModal}>
                    <div className="modal-content">
                        <h4>Modal Header</h4>
                        <p>A bunch of text</p>
                    </div>
                    <div className="modal-footer">
                        <button className="modal-close waves-effect waves-green btn-flat">Agree</button>
                    </div>
                </div>
            </nav>
        </>
    )
}
export default Navbar
