import React from 'react';
import {Link} from 'react-router-dom'
const [name, SetName] = useState("")
  const [password, SetPassword] = useState("")
  const [email, SetEmail] = useState("")

  const PostData = () => {
    if (/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email)) {
    fetch("/signup", {
      method: "post",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email,
        password
      })
    }).then(res => res.json())
      .then(data => {
        if (data.error) {
          return <Alerts type="error" message={data.error} />
        }
        else {
          history.push('/login')
          // <Alerts type="sucess" message={data.message} />
        }
      }).catch(err=>{
        console.log(err)
      })
    }else{
      return <Alerts type="sucess" message="invalid email" />
    }
  }



const Login = () => {
    return (
        <>
            <div className="mycard ">
                <div className="card auth-card input-field">
                    <h2 className="brand-logo">Instagram</h2>
                    <input type="text" placeholder="Email" ></input>
                    <input type="text" placeholder="Password" ></input>
                    <button className="btn waves-effect waves-light #64b5f6 blue lighten-2" type="submit" name="action">Login</button>
                    <Link to="/signup"> <h6>New User?click to signup</h6></Link>
                </div>
            </div>
        </>
    );
}

export default Login;
