import React, { useContext, useState } from 'react';
import { useEffect } from 'react';
import { UserContext } from '../../App'

const Profile = () => {
  const { state, dispatch } = useContext(UserContext)
  const [mypic, setMypic] = useState([])
  console.log(state)
  useEffect(() => {
    fetch("/mypost", {
      headers: {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    }).then(res => res.json())
      .then(result => {
        // console.log(result)
        setMypic(result.myposts)
      })
  }, [])
  return (
    <>
      <div className="main_profile">
        <div className="profile">
          <div className="profile-pic">
            <img src={state ? state.pic : ""} />
            <div className="file-field input-field">
              <div className="btn">
                <span>Upload Pic</span>
                <input type="file"  />
              </div>
              <div className="file-path-wrapper">
                <input className="file-path validate" type="text" />
              </div>
            </div>
            <button className="btn waves-effect waves-light #64b5f6 blue lighten-2" type="submit" name="action" ></button>
          </div>
          <div>
            <h4>{state.name}</h4>
            <div className="profile-status">
              <h5>{mypic.length} posts</h5>
              {/* <h5>{state.followers.length} followers</h5> */}
              {/* <h5>{state.following.length} followings</h5> */}
            </div>
          </div>
        </div>
      </div>
      <div className="gallery">
        {
          mypic.map(item =>
            <img key={item._id} src={item.photo} alt={item.title} />
          )
        }

      </div>
    </>
  );
}

export default Profile;
