    if(action.type==="USER")
    {
        return action.payload
    }
    if(action.type==="UPDATE")
    {
        return {
            ...state,
            followers:action.payload.followers,
            following:action.payload.followers
        }
    }
    if(action.type==="UPDATEPIC")
    {
        return {
            ...state,
pic        }
    }
    return state
}
